/////////////////////////////////////////////////////////////////////////////////
// CUBLASMatrixVectorOp implementation
CUBLASMatrixVectorOp::CUBLASMatrixVectorOp( CUBLASContext * pCUBLASContext ):
	m_hMatrixPositionA(), m_hMatrixRegionA(), m_hVectorPositionX(), m_hVectorPositionY()
{
	DebugAssert( pCUBLASContext != NULL && pCUBLASContext->IsCreated() );

	m_pCUBLASContext = pCUBLASContext;

	m_pMatrixA = NULL;
	m_pVectorX = NULL;
	m_pVectorY = NULL;
}
CUBLASMatrixVectorOp::~CUBLASMatrixVectorOp()
{
	// nothing to do
}


