/////////////////////////////////////////////////////////////////////////////////
// Third-Party Includes
#include <magma_v2.h>

/////////////////////////////////////////////////////////////////////////////////
// Includes
#include "MAGMAContext.h"

/////////////////////////////////////////////////////////////////////////////////
// MAGMAContext implementation
MAGMAContext::MAGMAContext()
{
	// nothing to do
}
MAGMAContext::~MAGMAContext()
{
	// nothing to do
}
	
Void Initialize() const
{
	magma_int_t iError = magma_init();
	Assert( iError == MAGMA_SUCCESS );
}
Void Cleanup() const
{
	magma_int_t iError = magma_finalize();
	Assert( iError == MAGMA_SUCCESS );
}

